import './App.css';
import { RadioList } from './components/RadioList';
import { Reproductor } from './components/Reproductor';
import { Search } from './components/Search';
import React, {useState, useEffect} from 'react';

function App() {
  const [data, setData] = useState([]);
  const [url, setUrl] = useState("");  
  const  [searchBarCont, setSearchBarCont] = useState("")

  const play = (url) => {
    setUrl(url);
  }

  const updateResults = (searchBarCont) => {
    const filterResult = data.filter(radio => {
      return `${radio.name.toLowerCase()}`.includes(searchBarCont.toLowerCase());
    })
    setData(filterResult)
    setSearchBarCont(searchBarCont)
  }

  useEffect(() => {
    fetch('http://91.132.145.114/json/stations?limit=10')
      .then(response => response.json())
      .then(json => {
        setData(json);
      })
      .catch(error => console.error(error));
  }, []);

  return (
    <>
      <Search onChange={updateResults} searchBarCont={searchBarCont}/>
      <Reproductor url={url}></Reproductor>
      <RadioList play={play} list={data} />
    </>
  );
}

export default App;
